mapper.update:
	UPDATE CLASS
	SET CLASSNAME 	= #{classname},
		CLASSTIME 	= #{classtime},
		CREDIT		= #{credit},
		MAXSTUDENT	= #{maxstudent}
	WHERE CLASSNUM 	= #{classnum}

mapper.select:
SELECT B.* FROM ( SELECT A.*,ROWNUM RN FROM (SELECT C.CLASSNUM, C.CLASSNAME, C.CREDIT, C.MAXSTUDENT, P.PROFESSORNAME, C.CLASSTIME, (SELECT COUNT(*) FROM CLASSAPPLY CA WHERE CA.CLASSNUM = C.CLASSNUM) APPLYCNT FROM CLASS C JOIN PROFESSOR P ON C.PROFESSORNUM = P.PROFESSORNUM WHERE 1=1
AND C.CLASSNUM = #{classnum}
AND C.CLASSNAME LIKE '%' || upper(#{classname}) || '%'
AND PROFESSORNAME LIKE '%' || #{professorname} || '%'
ORDER BY CLASSNUM ) A )B
WHERE RN BETWEEN #{start} AND #{end}

SELECT C.CLASSNUM, C.CLASSNAME FROM CLASS C JOIN CLASSAPPLY A ON C.CLASSNUM = A.CLASSNUM WHERE C.CLASSNUM = A.CLASSNUM
AND A.STUDENTNUM = #{value}
ORDER BY CLASSNUM

	SELECT 	COUNT(CLASSNAME)
	FROM	CLASS
	WHERE	CLASSNAME = #{classname}
