mapper.update:
		UPDATE HW SET 
						CLASSNUM=	#{classnum},	
						APPLYDATE =	#{applydate},
						HWNAME = 	#{hwname},	
						HWINFO = 	#{hwinfo}
						WHERE HWNUM = #{hwnum}

mapper.select:
SELECT HWNUM, TO_CHAR(APPLYDATE, 'YYYY-MM-DD') APPLYDATE, HWNAME, HWINFO, CLASSNUM, NVL( (SELECT '제출' FROM SUBMIT_HW A WHERE A.HWNUM = HW.HWNUM AND STUDENTNUM = #{studentnum} AND ROWNUM=1 ), '미제출') SUBMIT FROM HW WHERE 1=1
AND CLASSNUM = #{classnum}
ORDER BY HWNUM

SELECT H.HWNUM, H.HWNAME, H.APPLYDATE, H.CLASSNUM, H.HWINFO FROM HW H JOIN CLASS C ON H.CLASSNUM = C.CLASSNUM WHERE 1=1
AND C.PROFESSORNUM = #{professornum}
ORDER BY HWNUM
