mapper.select:
SELECT SEQ, TITLE, WRITER, CONTENT, REGDATE, CNT
FROM NOTICE
WHERE seq = #{seq}

mapper.insert:

	select max(seq)+1 from notice

INSERT INTO NOTICE(SEQ,TITLE,CONTENT,WRITER,REGDATE,CNT

	,UPLOADFILE

)VALUES(#{seq}, #{title}, #{content}, #{writer}, sysdate, 0

	,#{uploadfile}

)

mapper.update:
UPDATE NOTICE
SET TITLE=#{title},
	CONTENT=#{content},
	WRITER=#{writer}

	,UPLOADFILE=#{uploadfile}	

WHERE SEQ=#{seq}
